def encrypt(what):
    encoded = ""
    what = what.lower()
    for i in range(len(what)):
        encoded += codes[alph.index(what[i])]
    return encoded


def decrypt(what):
    decoded = ""
    for i in range(len(what)):
        decoded += alph[codes.index(what[i])]
    return decoded
